head.script:
function OptanonWrapper() {
  const categoriesConsentedTo = window.OnetrustActiveGroups;
  /* eslint-reason The underscores help keep this name unique and make the
   * variable name ugly, to dissuade users from referencing it unless absolutely
   * necessary (similar to React's dangerouslySetInnerHTML.__html prop) */

  /* eslint-disable-next-line no-underscore-dangle */

  window.__userHasConsentedToTargetingCookies = window.OnetrustActiveGroups.includes('C0004');
  const activeGroupsLoaded = new Event('OnetrustActiveGroupsLoaded');
  window.dispatchEvent(activeGroupsLoaded);
  window.OneTrust.OnConsentChanged(() => {
    if (categoriesConsentedTo === window.OnetrustActiveGroups) {
      return;
    }

    window.location.reload();
  });
}
